__global__ void kernel_ccsdT_sd1_1(double* t3, 
    double* d_t2_1, double* d_v2_1, 
    int size_h3,    int size_h2,    int size_h1,    int size_p6,    int size_p5,    int size_p4,    int size_h7, 
