                t3[t3_base_thread + (3 * stride_reg_y) + (2 * stride_reg_x)] = reg_tile[3][2];
                t3[t3_base_thread + (3 * stride_reg_y) + (3 * stride_reg_x)] = reg_tile[3][3];
            }
        }
    }
}

