                reg_tile[0][2] += t3[t3_base_thread + (0 * stride_reg_y) + (2 * stride_reg_x)];
                reg_tile[0][3] += t3[t3_base_thread + (0 * stride_reg_y) + (3 * stride_reg_x)];

                reg_tile[1][0] += t3[t3_base_thread + (1 * stride_reg_y) + (0 * stride_reg_x)];
