                reg_tile[2][1] += t3[t3_base_thread + (2 * stride_reg_y) + (1 * stride_reg_x)];
                reg_tile[2][2] += t3[t3_base_thread + (2 * stride_reg_y) + (2 * stride_reg_x)];
