    jk_ccsd_t_s1_3<<<gridsize, blocksize>>>(dev_t3, dev_t2, dev_v2, 
                                            size_h3, size_h2, size_h1, size_p6, size_p5, size_p4, 
                                            num_blks_h3, num_blks_h2, num_blks_h1, num_blks_p6, num_blks_p5, num_blks_p4, 
