// kernel: s1_9
__global__ void jk_ccsd_t_s1_9(double* d_t3, 
double* d_t2_9, double* d_v2_9,
size_t size_h3,     size_t size_h2,     size_t size_h1,     size_t size_p6,     size_t size_p5,     size_t size_p4, 
