			for (int idx_reg_x = 0; idx_reg_x < 4; idx_reg_x++) {
				// 
				if (idx_reg_y < rng_p6 && idx_reg_x < rng_p5) { 
          dev_t3_d[idx_reg_y * stride_reg_y + idx_reg_x * stride_reg_x] += op_c.reg[idx_reg_x + idx_reg_y * 4];
				}
			}
		}
	} 
}

