	if ((size_h3 - (blk_idx_h3 * SIZE_TILE_H3)) >= SIZE_TILE_H3)  { rng_h3 = SIZE_TILE_H3; }
	else                                                          { rng_h3 = size_h3 % SIZE_TILE_H3; }
	
