	// Related to Kernels
  // size_t numOperations = 2 * (size_t)(size_h3) * (size_t)(size_h2) * (size_t)(size_h1) * (size_t)(size_p6) * (size_t)(size_p5) * (size_t)(size_p4) * (size_t)(size_h7);
	
  // printf ("========================================= fusedKernels =============================================\n");
	// printf ("[%s] Grid Size (1D): %6d\n", __func__, numTbs);
