  next_unfused_kernel_d1_7<<<gridsize_1, blocksize_1, 2 * NUM_STAGE * 8 * STAGE_OFFSET, 0>>>(dev_t3, dev_t2, dev_v2, size_h3, size_h2, size_h1, size_p6, size_p5, size_p4, size_h7, 
