	const int tile_m = warp_id % 2; // 0:1
	const int tile_n = warp_id / 2; // 0:3

	MmaOperandC op_c;

	int internal_upperbound = 0;
	int internal_offset;

  //  
  //  based on sd2_1
  //  (p6,h2), (h1,h3)
	int idx_p6 = threadIdx.x % SIZE_TILE_P6; // this is not used for sd2. 
	int idx_h2 = threadIdx.x / SIZE_TILE_P6;
